import { createContext, useContext, useState, useEffect } from "react";

const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState({ username: null, data: null });

  // Load the user from local storage when the component is mounted
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    console.log(storedUser);
    if (storedUser) {
      setUser(user.data = JSON.parse(storedUser));
      console.log(user);
    }
  }, []);
  const fetchUsername = async (email) => {
    try {
      const response = await fetch(
        `/userInfo?email=${encodeURIComponent(email)}`
      );
      if (response.ok) {
        const data = await response.json();
        setUser((prevState) => ({ ...prevState, username: data.username }));
      } else {
        console.error(
          "Error fetching username:",
          response.status,
          response.statusText
        );
      }
    } catch (error) {
      console.error("Error fetching username:", error);
    }
  };

  // Save the user to local storage when it changes
  useEffect(() => {
    if (user) {
      localStorage.setItem("user", JSON.stringify(user));
    } else {
      localStorage.removeItem("user");
    }
  }, [user]);

  const login = (userData) => {
    setUser(userData);
    console.log(userData);
    fetchUsername(userData.email)
  };

  const logout = () => {
    setUser(null);
  };

  return (
    <UserContext.Provider
      value={{ user, setUser, login, logout, fetchUsername }}
    >
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => useContext(UserContext);
