window.noAdsPlease = false;

import React, { useEffect, useState, useRef } from "react";
import { Button } from "react-bulma-components";
import ReactQuill from "react-quill";
import _ from "lodash";
import "quill/dist/quill.snow.css";

function UserResponse() {
  const [value, setValue] = useState("");
  const [wordCount, setWordCount] = useState(0);
  const [isReadOnly, setIsReadOnly] = useState(false);
  const [noAdsPlease, setNoAdsPlease] = useState(false);
  const quillRef = useRef(null);

  useEffect(() => {
    console.log(value);
  }, [value]);
  
  useEffect(() => {
    // ... other useEffect code ...

    // Initialize the `noAdsPlease` state based on the `window.noAdsPlease` property
    if (window.noAdsPlease === undefined) {
      setNoAdsPlease(false);
    } else {
      setNoAdsPlease(window.noAdsPlease);
    }
  }, []);

  useEffect(() => {
    if (!noAdsPlease) {
      console.log("Adblock detected");
    }
  }, [noAdsPlease]);

  const handleChange = (content, delta, source, editor) => {
    const text = editor.getText().trim();
    const currentWordCount = text.length ? text.split(/\s+/).length : 0;
    if (isReadOnly) {
      if (currentWordCount <= 1000) {
        setValue(content);
        setWordCount(currentWordCount);
        setIsReadOnly(false);
      }
    } else {
      if (currentWordCount > 1000) {
        setIsReadOnly(true);
      }
      setValue(content);
      setWordCount(currentWordCount);
    }
  };



  return (
    <div>
      <ReactQuill
        ref={quillRef}
        theme="snow"
        className="editor-container"
        value={value}
        onChange={handleChange}
        readOnly={isReadOnly}
      />
      <p>Word count: {wordCount}</p>
      <Button className="submit-button mt-2" fullwidth={true}>
        Submit
      </Button>
    </div>
  );
}

export default UserResponse;
