import React, { useEffect, useState, useRef } from "react";
import { Button } from "react-bulma-components";
import ReactQuill from "react-quill";
import _ from "lodash";
import "quill/dist/quill.snow.css";
import blockAdBlock from "blockadblock";

function UserResponse() {
  const [value, setValue] = useState("");
  const [wordCount, setWordCount] = useState(0);
  const [isReadOnly, setIsReadOnly] = useState(false);
  const quillRef = useRef(null);

  useEffect(() => {
    console.log(value);
  }, [value]);

  const handleChange = (content, delta, source, editor) => {
    const text = editor.getText().trim();
    const currentWordCount = text.length ? text.split(/\s+/).length : 0;
    if (isReadOnly) {
      if (currentWordCount <= 1000) {
        setValue(content);
        setWordCount(currentWordCount);
        setIsReadOnly(false);
      }
    } else {
      if (currentWordCount > 1000) {
        setIsReadOnly(true);
      }
      setValue(content);
      setWordCount(currentWordCount);
    }
  };
  function adBlockNotDetected() {
    if (isReadOnly) {
      setIsReadOnly(false);
    }
  }
  
  function adBlockDetected() {
    setIsReadOnly(true);
  }
  
  // We look at whether BlockAdBlock already exists.
  if (typeof blockAdBlock !== "undefined" || typeof BlockAdBlock !== "undefined") {
    // If this is the case, it means that something tries to usurp our identity
    // So, considering that it is a detection
    adBlockDetected();
  } else {
    // Otherwise, you import the script BlockAdBlock
    var importFAB = document.createElement("script");
    importFAB.onload = function () {
      // If all goes well, we configure BlockAdBlock
      blockAdBlock.onDetected(adBlockDetected);
      blockAdBlock.onNotDetected(adBlockNotDetected);
    };
    importFAB.onerror = function () {
      // If the script does not load (blocked, integrity error, ...)
      // Then a detection is triggered
      adBlockDetected();
    };
    importFAB.integrity = "sha256-xjwKUY/NgkPjZZBOtOxRYtK20GaqTwUCf7WYCJ1z69w=";
    importFAB.crossOrigin = "anonymous";
    importFAB.src =
      "https://cdnjs.cloudflare.com/ajax/libs/blockadblock/3.2.1/blockadblock.min.js";
    document.head.appendChild(importFAB);
  }
  
  return (
    <div>
      <ReactQuill
        ref={quillRef}
        theme="snow"
        className="editor-container"
        value={value}
        onChange={handleChange}
        readOnly={isReadOnly}
      />
      <p>Word count: {wordCount}</p>
      <Button className="submit-button mt-2" fullwidth={true}>
        Submit
      </Button>
    </div>
  );
  

}

export default UserResponse;
