import React, { useEffect, useState } from 'react';
import { Box, Content } from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons'


function Prompt () {
    const [prompt, setPrompt] = useState('');
    const [promptIndex, setPromptIndex] = useState(0);
    const [writingPrompts, setWritingPrompts] = useState([]);
    const placeholder = ["1"]

    function getPrompts() {
        fetch('http://localhost:3001/currentPrompts')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            setWritingPrompts(data)
        })
    }


    function nextPrompt() {
    if (promptIndex < arrayWithIdAndText.length - 1)
        setPromptIndex(promptIndex + 1);
    else setPromptIndex(0);
    }
    function prevPrompt() {
    if (promptIndex > 0)
        setPromptIndex(promptIndex - 1);
    else setPromptIndex(arrayWithIdAndText.length - 1);
    }

    useEffect(() => {
        console.log(promptIndex)
    }, [promptIndex]);

useEffect(() => {
        getRandomPrompt();
    }, []);

    const getRandomPrompt = () => {
        const randomPrompt = placeholder[Math.floor(Math.random() * placeholder.length)];
        console.log(randomPrompt);
        setPrompt(randomPrompt);
    };

    
    return ( 
        <>
    <Box>
        <Content>
            <p className='is-size-4'>{placeholder[promptIndex].text}</p>
        </Content>
     
    </Box>
    <div className='is-flex is-justify-content-center mb-2'>
        <FontAwesomeIcon icon={faAngleLeft} size='2x' onClick={prevPrompt} className='has-text-white' />
        <p className='has-text-white is-size-5' onClick={getPrompts}>{promptIndex + 1}/{arrayWithIdAndText.length}</p>
        <FontAwesomeIcon icon={faAngleRight} size='2x' onClick={nextPrompt} className='has-text-white'/>
    </div>
    </>
     );
}

export default Prompt ;
