import React from 'react';
import { useEffect, useState } from 'react';
import { Box, Columns, Content, Heading } from 'react-bulma-components';



const stories = [
  {
    id: 1,
    prompt: 'A world where humans live alongside dragons.',
    author: 'John Doe',
    title: 'Dragons Among Us',
    story: 'Once upon a time, in a world where humans and dragons lived together...',
  },
  // Add more stories...
];

function Stories() {
    const [currentPromptStories, setCurrentPromptStories] = useState([]);

    useEffect(() => {
        fetch("http://localhost:3001/currentPromptStories")
       
            .then((response) => response.json())
            .then((data) => {
                setCurrentPromptStories(data);
            });
    }, []);

    useEffect(() => {
        console.log(currentPromptStories);
    }, [currentPromptStories]);


  return (
    <div>
      <Heading size={3} className="has-text-centered">
        User Stories
      </Heading>
      <Columns>
        {currentPromptStories.map((story) => (
          <Columns.Column key={story.id} size="one-third">
            <Box>
              <Heading size={4}>Title</Heading>
              <Heading subtitle size={6}>
                by {story[0]}
              </Heading>
              <Content>
                <p>
                  <strong>Prompt:</strong> {story.prompt}
                </p>
                <p>{story.story}</p>
              </Content>
            </Box>
          </Columns.Column>
        ))}
      </Columns>
    </div>
  );
}

export default Stories;
