import React, { useState } from 'react';
import { Box, Columns, Form, Button } from 'react-bulma-components';
const { Input, Field, Control, Label } = Form;

function SignUpLogin() {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const isValidPassword = (password) => {
    const minLength = 6;
    const hasNumber = /\d/;
    const hasSpecialChar = /[!@#$%^&*]/;
    const hasLetter = /[a-zA-Z]/;

    return (
      password.length >= minLength &&
      hasNumber.test(password) &&
      hasSpecialChar.test(password) &&
      hasLetter.test(password)
    );
  };

  const handleSignUpSubmit = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert('Passwords do not match.');
      return;
    }

    if (!isValidPassword(password)) {
      alert('Password must be at least 6 characters long, contain at least one letter, one number, and one special character.');
      return;
    }

    // Add your code to handle successful sign up here
    console.log('Sign up successful.');
  };

  return (
    <Columns className='signup-login-container is-vcentered is-multiline'>
      <Columns.Column className='login-section' id='login'>
        <Box className='login-box'>
          <h2 className='is-size-4 has-text-centered'>Log In</h2>
          <form>
            <Field>
              <Label>Email</Label>
              <Control>
                <Input type='email' placeholder='Email' />
              </Control>
            </Field>
            <Field>
              <Label>Password</Label>
              <Control>
                <Input type='password' placeholder='Password' />
              </Control>
            </Field>
            {/* Add a button for submitting the login form */}
          </form>
        </Box>
      </Columns.Column>
      <Columns.Column className='or-text is-hidden-touch is-2'>
        <p>or</p>
      </Columns.Column>
      <Columns.Column className='signup-section' id='signup'>
      <Box className='signup-box'>
        <h2 className='is-size-4 has-text-centered'>Sign Up</h2>
        <form onSubmit={handleSignUpSubmit}>
          {/* ... */}
          <Field>
            <Label>Password</Label>
            <Control>
              <Input
                type='password'
                placeholder='Password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Control>
          </Field>
          <Field>
            <Label>Confirm Password</Label>
            <Control>
              <Input
                type='password'
                placeholder='Confirm Password'
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </Control>
          </Field>
          <Button className='fullwidth-button' type='submit'>
            Sign Up
          </Button>
        </form>
      </Box>
    </Columns.Column>
    </Columns>
  );
}

export default SignUpLogin;
