import React, { useEffect, useState } from 'react';
import { Box, Content } from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'

function Prompt () {
    const [prompt, setPrompt] = useState('');
    const testPrompt = "In the remote village of Elmswood, nestled within the heart of an ancient forest, lies a hidden treasure: an enchanted orchard bearing fruits with magical properties. These fruits are carefully guarded by the villagers, who have learned to harness their powers for the betterment of their community. One day, a wandering stranger named Rowan arrives in Elmswood, bringing with them a mysterious past and a strong curiosity about the magical fruits."
  useEffect(() => {
    setPrompt(testPrompt);
    }, []);
    
    return ( 
        <>
    <Box>
        <Content>
            <p className='is-size-4'>{prompt}</p>
        </Content>
     
    </Box>
    <div className='is-flex is-justify-content-center'>
        <FontAwesomeIcon icon={faCoffee} size='2x' />
        <FontAwesomeIcon icon={faCoffee} size='2x' />
        <FontAwesomeIcon icon={faCoffee} size='2x' />
    </div>
    </>
     );
}

export default Prompt ;
