import React, { useEffect, useState } from 'react';
import { Box, Columns, Button } from 'react-bulma-components';
import { useUser } from './UserContext';

function StoryTracker() {

const [userPath, setUserPath] = useState('');
const [fileDiff, setFileDiff] = useState('');
const user = "tim";

async function getDiff(userName, file){
    const response = await fetch(`http://localhost:3001/diffFile`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify({userName, file}),
            });
    const data = await response.json();
    setFileDiff(data);
}

async function getFiles(userName){
    const response = await fetch('http://localhost:3001/getFiles', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify({userName}),
            });
    const data = await response.json();
    console.log(data);
}

useEffect(() => {
    getFiles(user);
}, []);


    return ( 
        <Columns>
            <Columns.Column size={3} className='px-0'>
                <Box className='files-box file-ui-box p-0'>

        
        
                </Box>
            </Columns.Column>
            <Columns.Column size={9} className='px-1'>
                <Box className='file-changes-box file-ui-box'>  
                </Box>
            </Columns.Column>
        </Columns>

     );


}

export default StoryTracker;
