import React, { useEffect, useState, useRef } from "react";
import { Button } from "react-bulma-components";
import ReactQuill from "react-quill";
import "quill/dist/quill.snow.css";
import BlockAdBlock from 'blockadblock';

function UserResponse() {
  const [value, setValue] = useState("");
  const [wordCount, setWordCount] = useState(0);
  const [isReadOnly, setIsReadOnly] = useState(false);
  const quillRef = useRef(null);

  useEffect(() => {
    console.log(value);
    const adBlocker = new BlockAdBlock({
      checkOnLoad: true,
      resetOnEnd: true,
      loopCheckTime: 50,
      loopMaxNumber: 5
    });
    if (adBlocker.isDetected()) {
      setIsReadOnly(true);
    }
  }, [value]);

  const handleChange = (content, delta, source, editor) => {
    const text = editor.getText().trim();
    const currentWordCount = text.length ? text.split(/\s+/).length : 0;
    if (currentWordCount <= 1000) {
      setValue(content);
      setWordCount(currentWordCount);
    } 
  };

  return (
    <div>
      <ReactQuill
        ref={quillRef}
        theme="snow"
        className="editor-container"
        value={value}
        onChange={handleChange}
        readOnly={isReadOnly}
      />
      <p>Word count: {wordCount}</p>
      <Button className="submit-button mt-2" fullwidth={true}>
        Submit
      </Button>
    </div>
  );
}

export default UserResponse;
