import React, { useEffect, useState, useRef } from 'react';
import { Box, Columns, Button, Table, Content } from 'react-bulma-components';
import { useUser } from './UserContext';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPaperPlane,
  faSearch
} from "@fortawesome/free-solid-svg-icons";


function StoryTracker() {

const [userPath, setUserPath] = useState('');
const [userFiles, setUserFiles] = useState([]);
const [fileDiff, setFileDiff] = useState('');
const fileChangesContentRef = useRef(null);

const user = "tim";

useEffect(() => {
    if (fileChangesContentRef.current) {
      fileChangesContentRef.current.innerHTML = fileDiff;
    }
  }, [fileDiff]);
  
  async function getFiles(userName) {
    const response = await fetch(`http://localhost:3001/getFiles?username=${userName}`);
    const data = await response.json();
    console.log(data);
    setUserFiles(data);

  }
  
  async function getDiff(userName, file) {
    const response = await fetch(`http://localhost:3001/getDiff?username=${userName}&fileName=${file}`);
    const diffData = await response.json();
  
    // Convert the diff JSON data into a human-readable format using the diff library
    const formattedDiff = diffData
      .map((entry) => {
        if (entry.added) {
          return `<ins style={"background-color:#e6ffe6;"}>${entry.value}</ins>`;
        } else if (entry.removed) {
          return `<del style="background-color:#ffe6e6;">${entry.value}</del>`;
        } else {
          return entry.value;
        }
      })
      .join('');
  
    setFileDiff(formattedDiff);
  }
  
useEffect(() => {
    getFiles(user);
}, []);


    return ( 
        <Columns>
            <Columns.Column size={4} className='px-0'>
                <nav className='panel'>
                <p className='panel-heading'>Story Tools</p>
                <div className='panel-block'>
                    <p className='control has-icons-left'>
                    <input className='input' type='text' placeholder='Search' />
                    <span className='icon is-left'>
                        <FontAwesomeIcon icon={faSearch} />
                    </span>
                    </p>
                </div>
                <Box className='file-ui-box-header'>
                    <p className='file-ui-box-header-text'>Files</p>
                    <Table className="table-files">
                    {userFiles.map((file) => (
                        <tr c las><td className='file-column-name'>
                        <p className='truncate scroll-on-hover' onClick={() => getDiff(user, file)}>{file}</p></td><td className='file-column-icon'><Button><FontAwesomeIcon icon={faPaperPlane}/></Button></td></tr>))}
                    </Table>

        
        
                </Box>
                </nav>
                </Columns.Column>
                <Columns.Column size={8} className="px-1">
                  <Box className="file-changes-box file-ui-box">
                      <p className="file-ui-box-header-text">File Changes</p>
                      <div ref={fileChangesContentRef} className="file-changes-content"></div>
                  
                  </Box>
                </Columns.Column>
              </Columns>

     );


}

export default StoryTracker;
