import React, { useEffect, useState } from 'react';
import { Box, Content } from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCoffee,
  faAngleLeft,
  faAngleRight,
  faThumbsUp,
  faThumbsDown,
} from '@fortawesome/free-solid-svg-icons';

function Prompt() {
  const [prompt, setPrompt] = useState('');
  const [promptIndex, setPromptIndex] = useState(0);
  const [writingPrompts, setWritingPrompts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/currentPrompts')
      .then(response => response.json())
      .then(data => {
        setWritingPrompts(data);
      });
  }, []);

  function nextPrompt() {
    if (promptIndex < writingPrompts.length - 1) setPromptIndex(promptIndex + 1);
    else setPromptIndex(0);
  }
  function prevPrompt() {
    if (promptIndex > 0) setPromptIndex(promptIndex - 1);
    else setPromptIndex(writingPrompts.length - 1);
  }

  useEffect(() => {
    console.log(promptIndex);
  }, [promptIndex]);

  return (
    <>
      <Box className="mb-2" style={{ position: 'relative' }}>
        <Content>
          <p className="is-size-5">
            {writingPrompts.length > 0 && writingPrompts[promptIndex].content}
          </p>
        </Content>
        <div className="like-dislike-buttons" style={{ position: 'absolute', bottom: 10, right: 10 }}>
          <FontAwesomeIcon icon={faThumbsUp} size="2x" className="has-text-white mr-3" />
          <FontAwesomeIcon icon={faThumbsDown} size="2x" className="has-text-white" />
        </div>
      </Box>

    </>
  );
}

export default Prompt;
