import React, { useState } from "react";
import { Button } from "react-bulma-components";
import ReactQuill from "react-quill";
import "quill/dist/quill.snow.css";
import { Link } from 'react-router-dom';

function UserResponse() {
  const [value, setValue] = useState("");
  const [wordCount, setWordCount] = useState(0);

  const countWords = (text) => {
    const trimmedText = text.trim();
    return trimmedText.length ? trimmedText.split(/\s+/).length : 0;
  };

  const handleChange = (content, delta, source, editor) => {
    const text = editor.getText();
    const currentWordCount = countWords(text);
    if (currentWordCount <= 100) {
      setValue(content);
      setWordCount(currentWordCount);
    } else {
      const words = text.trim().split(/\s+/).slice(0, 100).join(" ");
      const updatedContent = editor.setContents(editor.clipboard.convert(words), 'user');
      setValue(updatedContent);
      setWordCount(100);
    }
  };

  return (
    <div>
      <div className="quill-wrapper">
        <ReactQuill
          theme="snow"
          className={`editor-container`}
          value={value}
          onChange={handleChange}
        />
      </div>
      <p className="word-count">Word count: {wordCount}</p>
      <Button className="submit-button mt-2" fullwidth={true}>
        Submit
      </Button>
    </div>
  );
}

export default UserResponse;
