import React, { useState } from 'react';
import { Box, Columns } from 'react-bulma-components';


function About() {
    return ( 
        <Box className="about">
            <Columns>
                <Columns.Column>
                    <h1 className="is-size-1 has-text-centered">About</h1>
                    <p className="is-size-5 has-text-centered">PlotPilot is a writing prompt generator that helps you get started on your next story. It's a great way to get your creative juices flowing and to get your writing muscles warmed up. You can also use it to get your creative juices flowing if you're stuck on a story you're already working on. It's a great way to get your creative juices flowing and to get your writing muscles warmed up. You can also use it to get your creative juices flowing if you're stuck on a story you're already working on.</p>
                </Columns.Column>
            </Columns>
        </Box>

     );
}

export default About;
