import React, { useEffect, useState } from 'react';
import { Box, Content } from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons'

function Prompt () {
    const [prompt, setPrompt] = useState('');
    const testPrompt1 = "In a world where people can see the color of others' emotions, a young painter discovers they can only see in black and white. Their life takes an unexpected turn when they meet someone whose emotions appear in vivid colors, unveiling the painter's hidden talents."
    const testPrompt2 = " In a society where everyone is born with a unique tattoo that represents their destiny, a young woman with an unidentifiable mark sets off on a journey to decipher its meaning. Along the way, she uncovers a hidden truth that threatens to change her world forever."
    const testPrompt3 = "A lonely lighthouse keeper discovers a magical seashell that allows him to communicate with a mermaid living in the depths of the ocean. As their friendship grows, they must find a way to bridge the gap between their two worlds before it's too late."
    const testPrompt4 = "A skilled clockmaker in a steampunk city accidentally creates a pocket watch that controls time itself. When the watch falls into the wrong hands, the clockmaker must race against the clock to recover it before the fabric of time unravels."
    const testPrompt5 = "An archeologist unearths an ancient artifact with the power to grant wishes, but each wish comes with a price. As chaos unfolds around her, she must decide between using the artifact to change her life or destroying it to save the world."
  
    const testPrompts = [testPrompt1, testPrompt2, testPrompt3, testPrompt4, testPrompt5];
    
    const testPrompt = testPrompts[Math.floor(Math.random() * testPrompts.length)];

    console.log(testPrompt);

  
    useEffect(() => {
    setPrompt(testPrompt);
    }, []);
    
    return ( 
        <>
    <Box>
        <Content>
            <p className='is-size-4'>{prompt}</p>
        </Content>
     
    </Box>
    <div className='is-flex is-justify-content-center mb-2'>
        <FontAwesomeIcon icon={faAngleLeft} size='2x' />
        <p>{}</p>
        <FontAwesomeIcon icon={faAngleRight} size='2x' />
    </div>
    </>
     );
}

export default Prompt ;
