import React, { useState, useEffect, useRef } from "react";
import { Box, Columns, Button, Table, Content } from 'react-bulma-components';
import { useUser } from './UserContext';

function TrackedChangesBox({ getDiff, selectedFile }) {
    const fileChangesContentRef = useRef(null);
    const [fileDiff, setFileDiff] = useState('');
    const user = useUser();

    useEffect(() => {
      async function fetchData() {
        const diffData = await getDiff(user.username, selectedFile);
        setFileDiff(diffData);
      }
    
      if (selectedFile) {
        fetchData();
      }
    
      if (fileChangesContentRef.current) {
        fileChangesContentRef.current.innerHTML = fileDiff;
        console.log(fileChangesContentRef.current.innerHTML);
      }
      console.log(fileDiff);
    }, [getDiff, selectedFile, user.username]);

  return (
    <div className="file-changes-box tracked-changes-box">
      <p className="file-ui-box-header-text">File Changes</p>
      <div ref={fileChangesContentRef} className="file-changes-content">Test</div>
    </div>
  );
}

export default TrackedChangesBox;
